"use strict";(self.webpackChunksuomifi_design_system=self.webpackChunksuomifi_design_system||[]).push([[8455],{86005:function(t,a,e){e.r(a),e.d(a,{default:function(){return m}});var i=e(67294),s=e(28117),u=JSON.parse('{"TN":"Suomi.fi Design Systemin saavutettavuusseloste","NS":[{"paragraphs":[{"text":"Tässä saavutettavuusselosteessa kerrotaan, miten Suomi.fi Design System -sivustolla noudatetaan lakia digitaalisten palvelujen tarjoamisesta, mitä puutteita sivuston saavutettavuudessa on ja miten voit antaa meille palautetta saavutettavuusongelmista. Seloste on laadittu 19.9.2019."},{"text":"Tästä verkkopalvelusta vastaa Digi- ja väestötietovirasto. Haluamme, että mahdollisimman moni käyttäjä pystyy käyttämään digitaalisia palvelujamme. Otamme saavutettavuuden huomioon digitaalisten palvelujen kehityksessä. Palvelujen saavutettavuutta arvioidaan säännöllisesti osana suunnittelu- ja kehitystyötä."}]},{"title":"Miten saavutettavia sivut ovat?","paragraphs":[{"text":"Tämä sivusto täyttää lain vaatimat A- ja AA-tason saavutettavuuskriteerit (WCAG-kriteeristö 2.1). "}]},{"title":"Huomasitko puutteita saavutettavuudessa?","paragraphs":[{"text":"Pyrimme jatkuvasti huolehtimaan saavutettavuudesta. Jos löydät ongelmia, ilmoita niistä meille ja teemme parhaamme puutteiden korjaamiseksi. Vastaamme mahdollisimman pian tai viimeistään kahden viikon sisällä. Voit ottaa yhteyttä sähköpostitse osoitteella saavutettavuus(at)dvv.fi."}]},{"title":"Saavutettavuuden valvonta","paragraphs":[{"text":"Etelä-Suomen aluehallintovirasto valvoo saavutettavuusvaatimusten toteutumista. Jos et ole tyytyväinen saamaasi vastaukseen tai et saa vastausta lainkaan kahden viikon aikana, voit antaa palautteen Etelä-Suomen aluehallintovirastoon. Etelä-Suomen aluehallintoviraston sivulla kerrotaan tarkasti, miten valituksen voi tehdä ja miten asia käsitellään."}]},{"title":"Valvontaviranomaisen yhteystiedot","paragraphs":[{"text":"Etelä-Suomen aluehallintovirasto"}]},{"paragraphs":[{"text":"Saavutettavuuden valvonnan yksikkö"}]},{"paragraphs":[{"text":""}],"links":[{"text":"www.saavutettavuusvaatimukset.fi","url":"https://www.saavutettavuusvaatimukset.fi/"}]},{"paragraphs":[{"text":"sähköposti: saavutettavuus(at)avi.fi"}]},{"paragraphs":[{"text":"puhelin: vaihde 0295 016 000"}]},{"title":"Miten olemme testanneet saavutettavuutta?","paragraphs":[{"text":"Saavutettavuus on huomioitu Digi- ja väestötietoviraston digitaalisten palvelujen kehityksessä suunnittelusta toteutukseen kokonaisketterän mallin mukaisesti. Verkkosivuston saavutettavuutta on arvioinut Digi- ja väestötietoviraston hankkima saavutettavuusasiantuntija säännöllisesti osana suunnittelu- ja kehitysprosessia."}]}]}'),n=e(15083),l=e(10719),v=e(39657),o=e(39497),m=function(){return i.createElement(n.Z,null,i.createElement("div",{style:{padding:"0 "+s.R.spacing.xl}},i.createElement(l.Z,{title:u.TN}),i.createElement(v.X6,{variant:"h1"},u.TN),u.NS.map((function(t,a){return i.createElement(o.Z,{key:a,mainTitle:t.title,paragraphs:t.paragraphs,links:t.links})}))))}}}]);
